class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li class="comment-li">\n        <p>\n            <small class="comment-content">\n            ${e.content}\n            <br>\n                ${e.user.name}\n            </small>\n    \n            <small class="comment-func">\n               \n                    <small>\n                        <a class="delete-comment-button" href="/comments/destroy/${e._id}"><i class="fas fa-trash"></i>DELETE</a>\n                    </small>\n                    \n                \n                    <a class="toggle-like-button" data-likes="${e.likes.length}" href="/likes/toggle/?id=${e._id}&type=Comment">\n                        <i class="far fa-thumbs-up"></i> ${e.likes.length} Likes\n                    </a>        \n                \n                    ${e.likes.length} Likes\n                \n            </small>\n        </p>    \n    \n    </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}